/** @jsx jsx */
import { css, jsx } from "@emotion/core"
import { useState, useEffect } from "react"
import styled from "@emotion/styled";
import axios from "axios";
import tw from "tailwind.macro";

import Layout from "../components/Layout/Layout"
import { Title, SectionHeader, BodyText } from "../components/Typography/Typography"
import Artwork from "../components/Artwork/Artwork"

/*
                <span 
                    css={tw`${filter.isOn ? 'text-blue' : 'text-blue'}`}
                    onClick={() => {console.log('Click'); setfilter({...filter, isOn: true})}}
                >something</span>
                 / 
            <span css={tw`${filter.isOn ? 'text-grey' : 'text-black'}`}>anything</span>
*/
const Homepage = () => {
    const initialArtworks = [];
    const initialFilter = {
        isOn: false,
        maxYear: 2021,
        limit: 50,
        artist : null,
        type: null,
        title: null
    }
    const defaultModal = {
        shown: false,
        artwork: null
    }

    const [artworks, setartworks] = useState(initialArtworks);
    const [filter, setfilter] = useState(initialFilter);
    const [featuredArtwork, setFeaturedArtwork] = useState(defaultModal);

    const makeSearchQuery = term => `https://api.artic.edu/api/v1/artworks/search?q=${term}&limit=30&fields=id,title,image_id,date_end,artist_display,place_of_origin`
    const makePreviewImgSrc = imgId => `https://www.artic.edu/iiif/2/${imgId}/full/843,/0/default.jpg`

    // Data pull methods
    useEffect(() => {
        axios.get(makeSearchQuery("computer")).then(
            (res) => {
                let fixed = res.data?.data?.map((artwork) => ({
                        img_url: makePreviewImgSrc(artwork.image_id),
                        title: artwork.title,
                        origin: artwork.place_of_origin,
                        date: artwork.date_end,
                        artist: artwork.artist_display
                }));
                setartworks(fixed);
            }
        ).catch(e => console.log(e))
    }, [])
    // 

    return(
        <div css={tw`bg-white`}>
        <Layout css={tw``}>
            <Title css={tw`text-blue`}>Show me</Title>
            <Title css={tw`mb-6`}>
                anything
            </Title>
            <hr css={tw`border border-shadow mb-4`}/>
            <div css={css`
                display: grid;
                grid-auto-flow: row;
                gap: 2rem;
                grid-template-columns: repeat(auto-fit, minmax(20rem, 1fr))
            `}>
            { artworks.map(artwork => <Artwork artwork={artwork} onFeature={() => setFeaturedArtwork({shown: true, artwork: artwork})} />)}
            </div>
            { artworks.length == 0 
            ? <h1 css={tw`font-display text-blue`}>Be patient, art shall come</h1>
            : null
            }
        </Layout>
        </div>
    )
}

export default Homepage
